    func convert() -> String {
        guard let sourceCurrency = CurrencyManager.shared.sourceCurrency?.symbol, let destinationCurrency = CurrencyManager.shared.destinationCurrency?.symbol else {
            return "ERROR"
        }
        let amount = String(CurrencyManager.shared.amount ?? 0)
